mapper.select:
select user_email
from user
where user_email = #{userEmail}

mapper.update:
update user
set user_img = #{user_img}
where user_email = #{user_email}

update user
set user_password = #{new_password}
where user_email = #{user_email}
AND user_password = #{old_password}

update user
SET user_type = 0
where user_email = #{user_email}

mapper.select:
SELECT user_id
FROM user
WHERE user_email = #{email}
AND user_password = #{password}
